import React from 'react';
import { GoogleLogin } from 'react-google-login';
import { useHistory } from 'react-router-dom'; // Import useHistory from React Router

const responseGoogle = (response, history) => {
  // Handle Google login response
  console.log(response);

  if (response.profileObj) {
    // Access profile details if available
    const { imageUrl, name, email } = response.profileObj;
    console.log('Profile Photo URL:', imageUrl);
    console.log('Name:', name);
    console.log('Email:', email);

    // Redirect to the dashboard page on successful login
    history.push('/dashboard');
  } else {
    // Redirect to the login page on login failure
    history.push('/login');
  }
};

const GoogleLoginButton = () => {
  const history = useHistory(); // Access history from React Router

  return (
    <GoogleLogin
      clientId="YOUR_GOOGLE_CLIENT_ID"
      buttonText="Login with Google"
      onSuccess={(response) => responseGoogle(response, history)} // Pass history object to responseGoogle function
      onFailure={(response) => responseGoogle(response, history)} // Pass history object to responseGoogle function
      cookiePolicy={'single_host_origin'}
    />
  );
};

export default GoogleLoginButton;
