import io from "socket.io-client";
import { useState,use } from "react";
import Chat from "./chat";

const socket = io.connect("http://192.168.43.149:3001");
// http://192.168.43.149:3001
// http://192.168.193.161:3001
function App() {
  const [room, setRoom] = useState("");
  const [showChat, setShowChat] = useState(false);

  // Retrieve username from localStorage
  const username = localStorage.getItem('name');
   const photo=localStorage.getItem('photo');
  const joinRoom = () => {
    if (username !== "" && room !== "") {
      socket.emit("join_room", room);
      socket.emit("user_joined_send",username);
      setShowChat(true);
    }
  };
  useEffect(() => {
    console.log('Setting up listener for "user_joined_receive"...');
    socket.on("user_joined_receive", (message) => {
        console.log('Received message:', message);
    });
}, [socket]);
  return (
    <div className="App">
      {!showChat ? (
        <div className="joinChatContainer">
          <h3>Join A Chat</h3>
          <input
            type="text"
            placeholder="Room ID..."
            onChange={(event) => {
              setRoom(event.target.value);
            }}
          />
          <button onClick={joinRoom}>Join A Room</button>
        </div>
      ) : (
        <Chat socket={socket} username={username} room={room} photo={photo} />
      )}
    </div>
  );
}

export default App;
