import React, { useEffect, useState, useRef } from "react";
import { ArrowRightIcon, LogoutIcon, MenuIcon } from "@heroicons/react/outline";
import { MoonIcon, SunIcon, XIcon } from "@heroicons/react/solid";

import { GoogleLogout } from 'react-google-login';
import { useNavigate } from "react-router-dom";
// import Logo from "../chatlogo.png"
const Chat = ({ socket, username, room, photo }) => {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);
  const [istDateTime, setIstDateTime] = useState("");
  const [darkMode, setDarkMode] = useState(true);
  const userPhoto = localStorage.getItem('photo');
  const email = localStorage.getItem('email');
  const [showModal, setShowModal] = useState(false);
  const Navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const [userList, setUserList] = useState([]);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const toggleModal = () => {
    setShowModal(!showModal);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  
  }
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };
  const modalRef = useRef();

  const handleClickOutsideModal = (event) => {
    if (modalRef.current && !modalRef.current.contains(event.target)) {
      setShowModal(false);
    }
  };

  useEffect(() => {
    const handleWindowClick = (event) => {
      handleClickOutsideModal(event);
    };

    if (showModal) {
      window.addEventListener('click', handleWindowClick);
    }

    return () => {
      window.removeEventListener('click', handleWindowClick);
    };
  }, [showModal]);

  useEffect(() => {
    scrollToBottom();
  }, [messageList]);

  useEffect(() => {
    const interval = setInterval(() => {
      fetchIST();
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const fetchIST = () => {
    const now = new Date();
    const options = {
      hour: 'numeric',
      minute: 'numeric',
    };

    const formattedTime = now.toLocaleString('en-IN', options);
    const is12HourFormat = formattedTime.includes('AM') || formattedTime.includes('PM');

    setIstDateTime(is12HourFormat ? formattedTime : formattedTime.replace(':', '.'));
  };

  const sendMessage = async () => {
    const trimmedMessage = currentMessage.trim();
    if (trimmedMessage !== "") {
      const messageData = {
        room: room,
        photo: photo,
        author: username,
        message: trimmedMessage,
        time: istDateTime,
      };

      await socket.emit("send_message", messageData);

      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    socket.on("user", (message) => {
      console.log('Received message:', message.username);
      setUserList((list1) => [...list1, message]);
    });
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
  }, [socket]);
  //   useEffect(() => {
  //     // console.log('Setting up listener for "user_joined_receive"...');
  //     socket.on("user_joined_receive", (message) => {
  //         console.log('Received message:', message);
  //     });
  // }, [socket]);

  console.log(userList)
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };
  const buttonStyle = {
    backgroundColor: darkMode ? 'teal' : 'teal',
    color: darkMode ? '#fff' : '#fff',
    display: 'flex',
    padding: '5px',
    paddingLeft: '10px',
    paddingRight: '10px',
    borderRadius: 5
  };
  return (
    <div className={`min-h-screen flex flex-col relative ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-black'}`}>
      <div className={`fixed top-0 left-0 right-0 p-4 sm:p-4 ${darkMode ? 'bg-gray-800' : 'bg-gray-200'} z-10`}>
        {/* Dark mode toggle button */}


        {/* User modal */}
        <div className="relative" ref={modalRef}>
          <img
            src={userPhoto}
            alt="User Profile"
            className="fixed top-4 right-4 h-8 w-8 rounded-full cursor-pointer"
            onClick={toggleModal}
          />
          {showModal && (
            <div className={`fixed top-14 right-4 transform transition-all duration-300 ${showModal ? 'translate-x-0' : '-translate-x-full'}`}>
              <div className={`h-55 p-5 rounded-md flex flex-col items-center ${darkMode ? 'bg-gray-800 text-white' : 'bg-gray-300 text-black'}`}>
                <img
                  src={userPhoto}
                  alt="User Profile"
                  className="h-12 w-12 rounded-full mb-2"
                />
                <p className={`text-lg mb-4 ${darkMode ? 'text-white' : 'text-black'}`}>{username}</p>
                <p className={`text-xs mb-4 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>{email}</p>
                <button
                  onClick={toggleModal}
                  className="absolute top-2 right-1 px-3 py-1 rounded-md focus:outline-none flex items-center justify-center"
                  style={{ color: darkMode ? '#FFFFFF' : '#000000' }}
                >
                  <XIcon className="h-5 w-5" />
                </button>
                <button style={{ marginBottom: 15 }} onClick={toggleDarkMode}>
                  <div style={{ display: 'flex' }}>
                    <button onClick={toggleDarkMode} className={`p-2 rounded-full border ${darkMode ? 'border-gray-600 bg-gray-600' : 'border-gray-400 bg-gray-400'}`}>
                      {darkMode ? (
                        <SunIcon className="h-2 w-2 text-yellow-400" />
                      ) : (
                        <MoonIcon className="h-2 w-2 text-gray-500" />
                      )}
                    </button>&nbsp;&nbsp;<p className={`text-sm`} style={{ marginTop: '5px' }}> {!darkMode ? 'Dark Mode' : 'Light Mode'} </p>
                  </div>
                </button>

                <GoogleLogout
                  clientId="798504094336-id3r3n1ldom2rjmsa6tdp6db0oii5nct.apps.googleusercontent.com"
                  buttonText="SignOut"
                  onLogoutSuccess={() => Navigate('/')}
                  cookiePolicy={'single_host_origin'}
                  render={renderProps => (
                    <button onClick={renderProps.onClick} style={buttonStyle}>  <LogoutIcon className="h-5 w-5" />&nbsp;<p className={`text-sm mt-0.5`}>Sign Out</p></button>
                  )}
                />
              </div>
            </div>
          )}
        </div>
        {/* Chat header */}
        <div>
      {/* Mobile view */}
      <div className="sm:hidden fixed top-2 left-3">
        <button
          onClick={toggleDropdown}
          style={{
            background: 'none',
            border: 'none',
            cursor: 'pointer',
          }}
        >
         
        </button>
        {/* Dropdown content for mobile */}
        {isDropdownOpen && (
          <div className="bg-white p-2 mt-2 rounded shadow-md">
            <p className="text-black text-xs mt-1">ID: {room}</p>
            <button
              onClick={() => window.location.reload()}
              className="bg-red-600 hover:bg-red-400 text-white text-xs px-2 py-1 mt-2 rounded focus:outline-none focus:bg-teal-600"
            >
              Leave
            </button>
          </div>
        )}
      </div>
      {/* Desktop view */}
      <div className="hidden sm:flex fixed top-2 left-3">
        <p className="text-white-500 sm:text-xl text-xs mt-4 mr-3">ID: {room}</p>
        <button
          onClick={() => window.location.reload()}
          className="bg-red-600 hover:bg-red-400 text-white text-xs px-4 py-1 ml-5 mt-3 rounded focus:outline-none focus:bg-teal-600"
        >
          Leave
        </button>
      </div>
    </div>

        <h1 className={`text-xl sm:text-2xl font-semibold ${darkMode ? 'text-white' : 'text-gray-800'}`}>Chat <span style={{ color: 'teal' }}> HUB</span></h1>

      </div>


      {/* Message container */}
      <div className={`mt-16 flex-1 p-4 pb-20 ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-black'}`}>
        {/* <div className="fixed top-0 left-0 right-0 bottom-0 flex justify-center items-center"> */}
        <div  >
          {userList.map((messageContent, index) => {
            return (
              <div className={`relative mb-2 flex justify-center`} key={index}>
                <p style={{ color: !darkMode ? 'darkgray' : 'gray' }}> {messageContent.username}</p> &nbsp;&nbsp;joined !<br />
              </div>
            )
          })}
        </div>
        {/* </div> */}
        {messageList.map((messageContent, index) => {
          const isMe = messageContent.author === username;

          return (
            <div
              key={index}
              className={`relative mb-2 ${isMe ? 'flex justify-end' : 'flex justify-start'}`}
              id={isMe ? "you" : "other"}
            >
              {!isMe && (
                <div style={{ display: 'flex' }}>
                  <img
                    src={messageContent.photo}
                    alt="User Profile"
                    className="h-6 w-6 rounded-full"
                  />
                </div>
              )}
              <div style={{ marginBottom: 10, marginLeft: 10 }}>
                <div className={`${isMe && darkMode ? 'bg-teal-700' : isMe && !darkMode ? 'bg-teal-300' : !isMe && !darkMode ? 'bg-gray-200' : 'bg-gray-800'} ${darkMode ? 'text-white' : 'text-black'} py-1 px-2 rounded-lg inline-block max-w-md break-words relative`}>

                  <div style={{ display: 'flex', flexDirection: 'column' }}>
                    {!isMe && (
                      <p style={{ fontSize: 'calc(0.4em + 0.5vw)', textAlign: 'left', color: darkMode ? 'lightgreen' : 'green', marginBottom: '5px' }}>
                        {messageContent.author}
                      </p>
                    )}
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                      <p style={{ fontSize: 'calc(0.6em + 0.5vw)', color: `${darkMode ? 'white' : 'black'}`, minWidth: '3vw', wordWrap: 'break-word', textAlign: 'left', marginBottom: '0px', maxWidth: '60vw' }}>
                        {messageContent.message}
                      </p>
                      <p style={{ fontSize: 'calc(0.4em + 0.4vw)', color: `${darkMode ? 'white' : 'black'}`, marginLeft: '10px', textAlign: 'right', marginTop: 'auto', flexShrink: 0 }}>
                        {messageContent.time}
                      </p>
                    </div>
                  </div>

                  <div className={`absolute ${isMe ? 'right-0' : 'left-0'} -top-1`}>
                    <div className={`w-2 h-4 ${isMe && darkMode ? 'bg-teal-700' : isMe && !darkMode ? 'bg-teal-300' : !isMe && !darkMode ? 'bg-gray-200' : 'bg-gray-800'} transform rotate-180 rounded`}></div>
                  </div>

                </div>
              </div>

            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>

      {/* Input area */}
      <div className={`fixed bottom-0 left-0 right-0  ${darkMode ? 'border-gray-700 bg-gray-800 text-white' : 'border-gray-300 bg-gray-200 text-black'} p-4 sm:p-4`}>
        <div className="flex flex-col sm:flex-row">

          <input
            type="text"
            value={currentMessage}
            onChange={(event) => {
              setCurrentMessage(event.target.value);
            }}
            onKeyPress={(event) => {
              event.key === "Enter" && sendMessage();
            }}
            placeholder="Type a message..."
            className={`flex-1 p-2 rounded-md border ${darkMode ? 'border-gray-700 bg-gray-800 text-white' : 'border-gray-300 bg-gray-200 text-black'} focus:outline-none focus:ring focus:border-blue-500 mb-2 sm:mb-0 sm:mr-2`}
          />
          <button
            onClick={sendMessage}
            className="w-full sm:w-auto px-3 py-1 bg-teal-700 text-white rounded-md hover:bg-teal-600 focus:outline-none focus:ring focus:bg-teal-700"
          >
            <ArrowRightIcon className="h-6 w-6" />
          </button>

        </div>
      </div>
    </div>
  );
}

export default Chat;
