import React, { useEffect, useState, useRef } from "react";
import { ArrowRightIcon } from "@heroicons/react/outline";
import { MoonIcon, SunIcon, XIcon } from "@heroicons/react/solid";

const Chat = ({ socket, username, room, photo }) => {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);
  const [istDateTime, setIstDateTime] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const userPhoto = localStorage.getItem('photo');
  const email = localStorage.getItem('email');
  const [showModal, setShowModal] = useState(false);

  const messagesEndRef = useRef(null);
  const toggleModal = () => {
    setShowModal(!showModal);
  };
  const handleClickOutsideModal = (event) => {
    if (showModal && !event.target.closest('.fixed.top-4.left-0')) {
      setShowModal(false);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };
  useEffect(() => {
    window.addEventListener('click', handleClickOutsideModal);

    return () => {
      window.removeEventListener('click', handleClickOutsideModal);
    };
  }, [showModal]);
  useEffect(() => {
    scrollToBottom();
  }, [messageList]);

  useEffect(() => {
    const interval = setInterval(() => {
      fetchIST();
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const fetchIST = () => {
    const now = new Date();
    const options = {
      hour: 'numeric',
      minute: 'numeric',
    };

    const formattedTime = now.toLocaleString('en-IN', options);
    const is12HourFormat = formattedTime.includes('AM') || formattedTime.includes('PM');

    setIstDateTime(is12HourFormat ? formattedTime : formattedTime.replace(':', '.'));
  };

  const sendMessage = async () => {
    const trimmedMessage = currentMessage.trim();
    if (trimmedMessage !== "") {
      const messageData = {
        room: room,
        photo: photo,
        author: username,
        message: trimmedMessage,
        time: istDateTime,
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
  }, [socket]);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={`min-h-screen flex flex-col relative ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-black'}`}>
      <div className={`fixed top-0 left-0 right-0 p-4 sm:p-4 ${darkMode ? 'bg-gray-800' : 'bg-gray-200'} border-b border-gray-300 z-10`}>
        {/* Dark mode toggle button */}
        <button onClick={toggleDarkMode} className={`fixed top-4 right-4 p-2 rounded-full border ${darkMode ? 'border-gray-600 bg-gray-600' : 'border-gray-400 bg-gray-400'}`}>
          {darkMode ? (
            <SunIcon className="h-4 w-4 text-yellow-400" />
          ) : (
            <MoonIcon className="h-4 w-4 text-gray-500" />
          )}
        </button>

        {/* User modal */}
        <div className="relative">
          <img
            src={userPhoto}
            alt="User Profile"
            className="fixed top-4 right-15 h-8 w-8 rounded-full cursor-pointer"
            onClick={toggleModal}
          />
          {showModal && (
        <div className={`fixed top-4 left-0 transform transition-all duration-300 ${showModal ? 'translate-x-0' : '-translate-x-full'}`}>
          <div className={`h-55 p-5 rounded-md border border-gray-300 flex flex-col items-center ${darkMode ? 'bg-gray-800 text-white' : 'bg-white text-black'}`}>
            <img
              src={userPhoto}
              alt="User Profile"
              className="h-12 w-12 rounded-full mb-2"
            />
            <p className={`text-lg mb-4 ${darkMode ? 'text-white' : 'text-black'}`}>{username}</p>
            <p className={`text-xs mb-4 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>{email}</p>
            <button
              onClick={toggleModal}
              className="absolute top-2 right-2 px-3 py-1 rounded-md focus:outline-none flex items-center justify-center"
              style={{ color: darkMode ? '#FFFFFF' : '#000000' }}
            >
              <XIcon className="h-5 w-5" />
            </button>
          </div>
        </div>
      )}
        </div>

        {/* Chat header */}
        <h1 className={`text-xl sm:text-2xl font-semibold ${darkMode ? 'text-white' : 'text-gray-800'}`}>Strange CHAT...</h1>
      </div>

      {/* Message container */}
      <div className={`mt-16 flex-1 p-4 pb-20 ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-black'}`}>
        {messageList.map((messageContent, index) => {
          const isMe = messageContent.author === username;

          return (
            <div
              key={index}
              className={`relative mb-2 ${isMe ? 'flex justify-end' : 'flex justify-start'}`}
              id={isMe ? "you" : "other"}
            >
              {!isMe && (
                <div style={{ display: 'flex' }}>
                  <img
                    src={messageContent.photo}
                    alt="User Profile"
                    className="h-6 w-6 rounded-full"
                  />
                </div>
              )}
              <div style={{ marginBottom: 10, marginLeft: 10 }}>
                <div className={`${isMe && darkMode ? 'bg-teal-700' : isMe && !darkMode ? 'bg-teal-300' : !isMe && !darkMode ? 'bg-gray-200' : 'bg-gray-800'}  ${darkMode ? 'text-white' : 'text-black'} py-2 px-3 rounded-lg inline-block max-w-md break-words relative`}>


                  {!isMe && (
                    <p style={{ fontSize: 'calc(0.4em + 0.5vw)', textAlign: 'left', color: darkMode ? 'lightgreen' : 'green' }}>
                      {messageContent.author}
                    </p>
                  )}
                  <div style={{ display: 'flex' }}>
                    <p style={{ fontSize: 'calc(0.6em + 0.5vw)', color: `${darkMode ? 'white' : 'black'}`, textAlign: 'left', wordWrap: 'break-word', maxWidth: '70vw' }}>
                      {messageContent.message}
                    </p>
                    <p style={{ fontSize: 'calc(0.4em + 0.4vw)', marginTop: 'auto', textAlign: 'right', marginLeft: '10px', flexShrink: 0 }}>
                      {messageContent.time}
                    </p>
                  </div>

                  <div className={`absolute ${isMe ? 'right-0' : 'left-0'} -top-1`}>
                    <div className={`w-4 h-4 ${isMe && darkMode ? 'bg-teal-700' : isMe && !darkMode ? 'bg-teal-300' : !isMe && !darkMode ? 'bg-gray-200' : 'bg-gray-800'} transform rotate-180 rounded`}></div>

                  </div>
                </div>
              </div>

            </div>
          );
        })}
        <div ref={messagesEndRef} />
      </div>

      {/* Input area */}
      <div className={`fixed bottom-0 left-0 right-0  ${darkMode ? 'border-gray-700 bg-gray-800 text-white' : 'border-gray-300 bg-white text-black'} p-4 sm:p-4`}>
        <div className="flex flex-col sm:flex-row">
          <input
            type="text"
            value={currentMessage}
            onChange={(event) => {
              setCurrentMessage(event.target.value);
            }}
            onKeyPress={(event) => {
              event.key === "Enter" && sendMessage();
            }}
            placeholder="Type a message..."
            className={`flex-1 p-2 rounded-md border ${darkMode ? 'border-gray-700 bg-gray-800 text-white' : 'border-gray-300 bg-white text-black'} focus:outline-none focus:ring focus:border-blue-500 mb-2 sm:mb-0 sm:mr-2`}
          />
          <button
            onClick={sendMessage}
            className="w-full sm:w-auto px-3 py-1 bg-teal-700 text-white rounded-md hover:bg-teal-600 focus:outline-none focus:ring focus:bg-teal-700"
          >
            <ArrowRightIcon className="h-6 w-6" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
