// App.js

import React, { useState } from "react";
import io from "socket.io-client";
import Chat from "./chat";
import { Link } from 'react-router-dom';

const socket = io.connect("http://localhost:3001");

function App() {
  const [showChat, setShowChat] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [generatedCode, setGeneratedCode] = useState("");
  const [isChecked, setIsChecked] = useState(false);

  const username = localStorage.getItem('name');
  const photo = localStorage.getItem('photo');

  const generateRoomCode = () => {
    if (isChecked) {
      const alphanumeric = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
      let code = '';
      for (let i = 0; i < 6; i++) {
        code += alphanumeric.charAt(Math.floor(Math.random() * alphanumeric.length));
      }
      setGeneratedCode(code);
    } else {
      alert('Please agree to the terms and conditions.');
    }
  };

  const handleCheckboxChange = (event) => {
    setIsChecked(event.target.checked);
  };

  const joinRoom = () => {
    const messageData = {
      room: generatedCode,
      username: username
    };

    if (username !== "" && generatedCode !== "") {
      setIsLoading(true); // Set loading here before emitting events
      socket.emit("join_room", generatedCode);
      socket.emit("user_joined_send", messageData);
      setShowChat(true);
      setTimeout(() => {
        setIsLoading(false); // Set loading false after some delay (for simulation)
      }, 3000); // Simulate a 3-second delay before disabling the loading state
    }
  };

  return (
    <div className="App">
      {!showChat ? (
        <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
          <div className="flex items-center justify-center">
            <div className="ml-10 mr-10 bg-gray-700 rounded shadow p-8">
              <h3 className="text-xl mb-6 text-teal-500 k font-semibold">Join A Chat..!</h3>

              <div className="App">
                <button
                  onClick={generateRoomCode}
                  className="bg-gray-500 hover:bg-gray-400 text-white font-bold py-2 px-4 rounded focus:outline-none focus:bg-gray-400 relative mb-5"
                >
                  Generate New Code
                </button>
                <p className="text-white">or</p>
                <b className="text-white">Existing Code..</b>
                <input
                  type="text"
                  placeholder="Enter Room ID..."
                  value={generatedCode}
                  className="block w-full border rounded py-2 px-4 mt-2 mb-4 text-black focus:outline-none focus:border-teal-500"
                  onChange={(event) => {
                    setGeneratedCode(event.target.value);
                  }}
                />
              </div>
              <p className="text-left text-xs sm:text-sm md:text-base lg:text-sm text-gray-400 mb-4 sm:mb-4 mt-10">
                By joining, you agree to our <Link to="/terms" className="underline">Terms and Conditions</Link>.
              </p>

              <div className="flex items-center mb-4 sm:mb-4">
                <input
                  type="checkbox"
                  checked={isChecked}
                  onChange={handleCheckboxChange}
                  className="mr-1 h-2 w-2 sm:h-3 sm:w-3 md:h-4 md:w-4 lg:h-4 lg:w-4"
                />
                <label htmlFor="agree" className="text-xs sm:text-xs md:text-sm lg:text-base text-gray-200">
                  I agree to the terms and conditions
                </label>
              </div>
              <button
                onClick={joinRoom}
                className="bg-teal-500 hover:bg-teal-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:bg-teal-600 relative"
                disabled={isLoading}
              >
                {isLoading ? (
                  <>
                    <svg className="animate-spin h-5 w-5 mr-3" viewBox="0 0 24 24">
                      {/* Your SVG for loading spinner */}
                    </svg>
                    Joining...
                  </>
                ) : (
                  'Join A Room'
                )}
              </button>
            </div>
          </div>
        </div>
      ) : (
        <Chat socket={socket} username={username} room={generatedCode} photo={photo} />
      )}
    </div>
  );
}

export default App;
