import io from "socket.io-client";
import { useState } from "react";
import Chat from "./chat";
import { Link } from 'react-router-dom';
import Logo from "../chatlogo.png"

const socket = io.connect("http://192.168.193.161:3001");

function App() {
  const [room, setRoom] = useState("");
  const [showChat, setShowChat] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [generatedCode, setGeneratedCode] = useState("");
  const [isChecked, setIsChecked] = useState(false);

  const username = localStorage.getItem('name');
  const photo = localStorage.getItem('photo');

  const generateRoomCode = () => {
    const alphanumeric = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let code = '';
    for (let i = 0; i < 6; i++) {
      code += alphanumeric.charAt(Math.floor(Math.random() * alphanumeric.length));
    }
    setGeneratedCode(code);
    setShowChat(true); // Redirect to Chat component
  };

  const handleCheckboxChange = (event) => {
    setIsChecked(event.target.checked);
  };

  const handleJoinRoom = () => {
    if (isChecked && room === generatedCode) {
      joinRoom();
    } else {
      alert('Please agree to the terms and conditions and enter the correct room code.');
    }
  };

  const joinRoom = () => {
    const messageData = {
      room: room,
      username: username
    };

    if (username !== "" && room !== "") {
      socket.emit("join_room", room);
      socket.emit("user_joined_send", messageData);
      setShowChat(true); // Redirect to Chat component
    }
  };

  return (
    <div className="App">
      {!showChat ? (
        <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
          <img className="fixed top-5 left-35 h-20 w-21 cursor-pointer" src={Logo} alt="Logo" />
          <div className="flex items-center justify-center">
            <div className="ml-10 mr-10 bg-gray-200 rounded shadow p-8">
              <h3 className="text-lg mb-4 text-black font-semibold">Join A Chat</h3>
              <p className="text-left text-xs sm:text-sm md:text-base lg:text-sm text-gray-600 mb-4 sm:mb-4">
                By joining, you agree to our <Link to="/terms" className="underline">Terms and Conditions</Link>.
              </p>

              <div className="flex items-center mb-4 sm:mb-4">
                <input
                  type="checkbox"
                  checked={isChecked}
                  onChange={handleCheckboxChange}
                  className="mr-1 h-2 w-2 sm:h-3 sm:w-3 md:h-4 md:w-4 lg:h-4 lg:w-4"
                />
                <label htmlFor="agree" className="text-xs sm:text-xs md:text-sm lg:text-base text-gray-800">
                  I agree to the terms and conditions
                </label>
              </div>

              <div className="App">
                <button
                  onClick={generateRoomCode}
                  className="bg-teal-500 hover:bg-teal-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:bg-teal-600 relative mb-10"
                >
                  Create Room
                </button>
                <p>Join using Code..</p>
                <input
                  type="text"
                  placeholder="Enter Room ID..."
                  className="block w-full border rounded py-2 px-4 mb-4 text-black focus:outline-none focus:border-teal-500"
                  onChange={(event) => {
                    setRoom(event.target.value);
                  }}
                />
                <button
                  onClick={handleJoinRoom}
                  className="bg-teal-500 hover:bg-teal-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:bg-teal-600 relative"
                  disabled={isLoading}
                >
                  {isLoading ? <span className="ml-2">Joining...</span> : 'Join A Room'}
                </button>
                {generatedCode && <p>Room Code: {generatedCode}</p>}
              </div>
            </div>
          </div>
        </div>
      ) : (
        <Chat socket={socket} username={username} room={room} photo={photo} />
      )}
    </div>
  );
}

export default App;
