import React, { useEffect, useState } from "react";
import { ArrowRightIcon } from "@heroicons/react/outline";
function Chat({ socket, username, room }) {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);
  const [istDateTime, setIstDateTime] = useState("");
  useEffect(() => {
    const fetchIST = () => {
      const now = new Date();
      const options = {
        hour: 'numeric',
        minute: 'numeric',
      };

      const formattedTime = now.toLocaleString('en-IN', options);
      const is12HourFormat = formattedTime.includes("AM") || formattedTime.includes("PM");

      setIstDateTime(is12HourFormat ? formattedTime : formattedTime.replace(':', '.')); // Display based on system's time format
    };

    fetchIST(); // Call fetchIST function once when the component mounts
  }, []);
  const sendMessage = async () => {


    if (currentMessage !== "") {
      const messageData = {
        room: room,
        author: username,
        message: currentMessage,
        time:
        istDateTime,
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
}, [socket]);

return (
    <div className="min-h-screen bg-gray-100 flex flex-col relative">
    <div className="fixed top-0 left-0 right-0 p-4 sm:p-4 bg-gray-200 border-b border-gray-300 z-10">
      <h1 className="text-xl sm:text-2xl text-2xl sm:text-3xl font-semibold text-gray-800">Strange CHAT...</h1>
    <h1>
    </h1>
    </div>
    <div className="mt-16 flex-1 overflow-y-auto p-4 sm:p-6 pb-24">

    {messageList.map((messageContent, index) => {
  const isMe = messageContent.author === username;

  return (
    <div
      key={index}
      className={`relative mb-2 ${isMe ? 'flex justify-end' : 'flex justify-start'}`}
      id={isMe ? "you" : "other"}
    >
      <p>messageContent.a</p>
      <div className={`bg-${isMe ? 'teal-700' : 'gray-200'} text-black py-2 px-4 rounded-lg inline-block max-w-xs break-words`}>
        <p  style={{ textAlign: 'left',fontSize:'small' }}>{messageContent.message}</p>
        <p style={{ alignSelf: 'right' ,fontSize:'x-small'}}>{messageContent.time}</p>
      </div>
      <div className={`absolute ${isMe ? 'right-0' : 'left-0'}`}>
        <div className={`w-4 h-4 ${isMe ? 'bg-teal-700' : 'bg-gray-200'} transform rotate-90 -mt-1 top-0 rounded-full`}></div>
      </div>
    </div>
  );
})}

       
      </div>
     
      <div className="fixed bottom-0 left-0 right-0 bg-gray-200 border-t border-gray-300 p-4 sm:p-4">
      <div className="flex flex-col sm:flex-row">
        <input
          type="text"
          value={currentMessage}
          onChange={(event) => {
            setCurrentMessage(event.target.value);
          }}
          onKeyPress={(event) => {
            event.key === "Enter" && sendMessage();
          }}
          placeholder="Type a message..."
          className="flex-1 p-2 rounded-md border border-gray-300 focus:outline-none focus:ring focus:border-blue-500 mb-2 sm:mb-0 sm:mr-2"
        />
        <button
          onClick={sendMessage}
          className="w-full sm:w-auto px-3 py-1 bg-teal-700 text-white rounded-md hover:bg-teal-600 focus:outline-none focus:ring focus:bg-teal-700"
        >
          <ArrowRightIcon className="h-6 w-6" />
        </button>
      </div>
    </div>
    </div>
  );
}

export default Chat;
