import io from "socket.io-client";
import { useState, useEffect } from "react";
import Chat from "./chat";
import { Link } from 'react-router-dom';
import Logo from "../chatlogo.png"
const socket = io.connect("http://192.168.193.161:3001");

function App() {
  const [room, setRoom] = useState("");
  const [showChat, setShowChat] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const username = localStorage.getItem('name');
  const photo = localStorage.getItem('photo');
  const [isChecked, setIsChecked] = useState(false);

  const handleCheckboxChange = (event) => {
    setIsChecked(event.target.checked);
  };

  const handleJoinRoom = () => {
    if (isChecked) {
      setIsLoading(true); // Show loader
      setTimeout(() => {
        joinRoom();
        setIsLoading(false); // Hide loader after 3 seconds
      }, 2000);
    } else {
      alert('Please agree to the terms and conditions.');
    }
  };

  const joinRoom = () => {
    const messageData = {
      room: room,
      username: username
    };

    if (username !== "" && room !== "") {
      socket.emit("join_room", room);
      socket.emit("user_joined_send", messageData);
      setShowChat(true);
    }
  };

  return (
    <div className="App">
      {!showChat ? (
        <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
          <img className="fixed top-5 left-35 h-20 w-21 cursor-pointer" src={Logo} alt="Logo" />
          <div className="flex items-center justify-center">
            <div className="ml-10 mr-10 bg-gray-200 rounded shadow p-8">
              <h3 className="text-lg mb-4 text-black font-semibold">Join A Chat</h3>
              <input
                type="text"
                placeholder="Enter Room ID..."
                className="block w-full border rounded py-2 px-4 mb-4 text-black focus:outline-none focus:border-teal-500"
                onChange={(event) => {
                  setRoom(event.target.value);
                }}
              />
              <p className="text-xs sm:text-sm md:text-base lg:text-lg text-gray-600 mb-2 sm:mb-4">
                By joining, you agree to our <Link to="/terms" className="underline">Terms and Conditions</Link>.
              </p>
              <div className="flex items-center mb-2 sm:mb-4">
                <input
                  type="checkbox"
                  checked={isChecked}
                  onChange={handleCheckboxChange}
                  className="mr-2 h-3 w-3 sm:h-4 sm:w-4 md:h-5 md:w-5 lg:h-6 lg:w-6"
                />
                <label htmlFor="agree" className="text-xs sm:text-sm md:text-base lg:text-lg text-gray-800">
                  I agree to the terms and conditions
                </label>
              </div>


              <div className="App">
                <button
                  onClick={handleJoinRoom}
                  className="bg-teal-500 hover:bg-teal-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:bg-teal-600 relative"
                  disabled={isLoading}
                >
                  <div className="flex items-center">



                    {isLoading && (<><svg aria-hidden="true" role="status" class="inline w-4 h-4 me-3 text-white animate-spin" viewBox="0 0 100 101" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M100 50.5908C100 78.2051 77.6142 100.591 50 100.591C22.3858 100.591 0 78.2051 0 50.5908C0 22.9766 22.3858 0.59082 50 0.59082C77.6142 0.59082 100 22.9766 100 50.5908ZM9.08144 50.5908C9.08144 73.1895 27.4013 91.5094 50 91.5094C72.5987 91.5094 90.9186 73.1895 90.9186 50.5908C90.9186 27.9921 72.5987 9.67226 50 9.67226C27.4013 9.67226 9.08144 27.9921 9.08144 50.5908Z" fill="#E5E7EB" />
                      <path d="M93.9676 39.0409C96.393 38.4038 97.8624 35.9116 97.0079 33.5539C95.2932 28.8227 92.871 24.3692 89.8167 20.348C85.8452 15.1192 80.8826 10.7238 75.2124 7.41289C69.5422 4.10194 63.2754 1.94025 56.7698 1.05124C51.7666 0.367541 46.6976 0.446843 41.7345 1.27873C39.2613 1.69328 37.813 4.19778 38.4501 6.62326C39.0873 9.04874 41.5694 10.4717 44.0505 10.1071C47.8511 9.54855 51.7191 9.52689 55.5402 10.0491C60.8642 10.7766 65.9928 12.5457 70.6331 15.2552C75.2735 17.9648 79.3347 21.5619 82.5849 25.841C84.9175 28.9121 86.7997 32.2913 88.1811 35.8758C89.083 38.2158 91.5421 39.6781 93.9676 39.0409Z" fill="currentColor" />
                    </svg><span className="ml-2">Joining...</span></>)}
                  </div>
                  {!isLoading && 'Join A Room'}
                </button>


              </div>

            </div>
          </div>
        </div>
      ) : (
        <Chat socket={socket} username={username} room={room} photo={photo} />
      )}
    </div>
  );
}

export default App;
