import React, { useEffect, useState } from "react";
import ScrollToBottom from "react-scroll-to-bottom";

function Chat({ socket, username, room }) {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);

  const sendMessage = async () => {
    if (currentMessage !== "") {
      const messageData = {
        room: room,
        author: username,
        message: currentMessage,
        time:
          new Date(Date.now()).getHours() +
          ":" +
          new Date(Date.now()).getMinutes(),
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
  }, [socket]);

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col relative">
    <div className="fixed top-0 left-0 right-0 p-4 sm:p-4 bg-gray-200 border-b border-gray-300 z-10">
      <h1 className="text-xl sm:text-2xl text-2xl sm:text-3xl font-semibold text-gray-800">Strange CHAT...</h1>
    </div>
    <div className="mt-16 flex-1 overflow-y-auto p-4 sm:p-6 pb-24">

          {messageList.map((messageContent) => {
            return (
              <div
                className={`relative mb-2 ${messageContent.from === 'user' ? 'flex justify-end' : 'flex justify-start'}`}
                id={username === messageContent.author ? "you" : "other"}
              >
              
                <div>
                  <div className={`bg-${messageContent.from === 'user' ? 'teal' : 'teal'}-700 text-black py-2 px-4 rounded-lg inline-block max-w-xs break-words`}>
                    <p>{messageContent.message}</p>
                  </div>
                  <div className={`absolute ${messageContent.from === 'user' ? 'right-0' : 'left-0'}`}>
            <div className={`w-4 h-4 ${message.from === 'user' ? 'bg-teal-200' : 'bg-teal-700'} transform rotate-90 -mt-1 top-0 rounded-full`}></div>
          </div>
                  <div className="message-meta">
                    <p id="time">{messageContent.time}</p>
                    <p id="author">{messageContent.author}</p>
                  </div>
                </div>
              </div>
            );
          })}
       
      </div>
      <div className="chat-footer">
        <input
          type="text"
          value={currentMessage}
          placeholder="Hey..."
          onChange={(event) => {
            setCurrentMessage(event.target.value);
          }}
          onKeyPress={(event) => {
            event.key === "Enter" && sendMessage();
          }}
        />
        <button onClick={sendMessage}>&#9658;</button>
      </div>
    </div>
  );
}

export default Chat;
