import io from "socket.io-client";
import { useState, useEffect } from "react";
import Chat from "./chat";
import { Link } from 'react-router-dom';

const socket = io.connect("http://192.168.193.161:3001");

function App() {
  const [room, setRoom] = useState("");
  const [showChat, setShowChat] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const username = localStorage.getItem('name');
  const photo = localStorage.getItem('photo');
  const [isChecked, setIsChecked] = useState(false);

  const handleCheckboxChange = (event) => {
    setIsChecked(event.target.checked);
  };

  const handleJoinRoom = () => {
    if (isChecked) {
      setIsLoading(true); // Show loader
      setTimeout(() => {
        joinRoom();
        setIsLoading(false); // Hide loader after 3 seconds
      }, 3000);
    } else {
      alert('Please agree to the terms and conditions.');
    }
  };

  const joinRoom = () => {
    const messageData = {
      room: room,
      username: username
    };
    
    if (username !== "" && room !== "") {
      socket.emit("join_room", room);
      socket.emit("user_joined_send", messageData);
      setShowChat(true);
    }
  };

  return (
    <div className="App">
      {!showChat ? (
        <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
          <div className="flex items-center justify-center">
            <div className="bg-gray-200 rounded shadow p-8">
              <h3 className="text-lg mb-4 text-black font-semibold">Join A Chat</h3>
              <input
                type="text"
                placeholder="Enter Room ID..."
                className="block w-full border rounded py-2 px-4 mb-4 text-black focus:outline-none focus:border-teal-500"
                onChange={(event) => {
                  setRoom(event.target.value);
                }}
              />
              <p className="text-sm text-gray-600 mb-4">
                By joining, you agree to our <Link to="/terms" className="underline">Terms and Conditions</Link>.
              </p>
              <div className="flex items-center mb-4">
                <input
                  type="checkbox"
                  checked={isChecked}
                  onChange={handleCheckboxChange}
                  className="mr-2"
                />
                <label htmlFor="agree" className="text-sm text-gray-800">
                  I agree to the terms and conditions
                </label>
              </div>
              
              <button
  onClick={handleJoinRoom}
  className="relative bg-teal-500 hover:bg-teal-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:bg-teal-600"
  disabled={isLoading} // Disable button while loading
>
  {isLoading ? (
    <div className="absolute inset-0 flex items-center justify-center">
      <svg className="animate-spin h-5 w-5 mr-3" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V2.5a.5.5 0 011 0V4a8 8 0 01-8 8z"></path>
      </svg>
      Joining...
    </div>
  ) : (
    'Join A Room'
  )}
</button>
            </div>
          </div>
        </div>
      ) : (
        <Chat socket={socket} username={username} room={room} photo={photo} />
      )}
    </div>
  );
}

export default App;
