const handleJoinRoom = () => {
  if (isChecked) {
    setIsLoading(true);
    joinRoomWithDelay();
  } else {
    alert('Please agree to the terms and conditions.');
  }
};

const joinRoomWithDelay = () => {
  // Simulate a 3-second delay before enabling the button again
  setTimeout(() => {
    joinRoom();
    setIsLoading(false);
  }, 3000); // 3 seconds delay
};
