import React, { useState } from 'react';
import { ArrowRightIcon } from '@heroicons/react/outline';
const ChatPage = ({socket,room,username}) => {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const handleMessageChange = (event) => {
      setInputMessage(event.target.value);
    };
  
    const sendMessage = async () => {
        if (inputMessage !== "") {
          const messageData = {
            room: room,
            author: username,
            message: inputMessage,
            time:
              new Date(Date.now()).getHours() +
              ":" +
              new Date(Date.now()).getMinutes(),
          };
    
          socket.emit("send_message", messageData);
          setMessages((list) => [...list, messageData]);
          setInputMessage("");
        }
      };
  
    return (
      <div className="min-h-screen bg-gray-100 flex flex-col relative">
        <div className="fixed top-0 left-0 right-0 p-4 sm:p-4 bg-gray-200 border-b border-gray-300 z-10">
          <h1 className="text-xl sm:text-2xl text-2xl sm:text-3xl font-semibold text-gray-800">Strange CHAT...</h1>
        </div>
  
        <div className="mt-16 flex-1 overflow-y-auto p-4 sm:p-6 pb-24">
  {messages.map((message, index) => (
    <div key={index} className={`relative mb-2 ${message.from === 'user' ? 'flex justify-end' : 'flex justify-start'}`}>
      <div className={`bg-${message.from === 'user' ? 'teal' : 'teal'}-700 text-black py-2 px-4 rounded-lg inline-block max-w-xs break-words`}>
        <p>Room: {message.room}</p>
        <p>Author: {message.author}</p>
        <p>Message: {message.message}</p>
        <p>Time: {message.time}</p>
      </div>
      <div className={`absolute ${message.from === 'user' ? 'right-0' : 'left-0'}`}>
        <div className={`w-4 h-4 ${message.from === 'user' ? 'bg-teal-200' : 'bg-teal-700'} transform rotate-90 -mt-1 top-0 rounded-full`}></div>
      </div>
    </div>
  ))}
</div>

  
        <div className="fixed bottom-0 left-0 right-0 bg-gray-200 border-t border-gray-300 p-4 sm:p-4">
          <div className="flex flex-col sm:flex-row">
            <input
              type="text"
              value={inputMessage}
              onChange={handleMessageChange}
              placeholder="Type a message..."
              className="flex-1 p-2 rounded-md border border-gray-300 focus:outline-none focus:ring focus:border-blue-500 mb-2 sm:mb-0 sm:mr-2"
            />
            <button
              onClick={sendMessage}
              className="w-full sm:w-auto px-3 py-1 bg-teal-700 text-white rounded-md hover:bg-teal-600 focus:outline-none focus:ring focus:bg-teal-700"
            >
              <ArrowRightIcon className="h-6 w-6" />
            </button>
          </div>
        </div>
      </div>
    );
  };
  
  export default ChatPage;
