import React, { useState } from 'react';
import { ArrowRightIcon } from '@heroicons/react/outline';
const ChatPage = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');

  const handleMessageChange = (event) => {
    setInputMessage(event.target.value);
  };

  const sendMessage = () => {
    if (inputMessage.trim() !== '') {
      setMessages([...messages, inputMessage]);
      setInputMessage('');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <div className="p-4 sm:p-6 bg-gray-200 border-b border-gray-300">
        <h1 className="text-xl sm:text-2xl font-semibold text-gray-800">Simple Chat</h1>
      </div>

      <div className="p-4 sm:p-6 flex-1 overflow-y-auto">
        {messages.map((message, index) => (
          <div key={index} className="mb-2">
            <p className="bg-blue-500 text-white py-2 px-4 rounded-md inline-block">
              {message}
            </p>
          </div>
        ))}
      </div>

      <div className="p-4 sm:p-6 bg-gray-200 border-t border-gray-300">
        <div className="flex flex-col sm:flex-row">
          <input
            type="text"
            value={inputMessage}
            onChange={handleMessageChange}
            placeholder="Type a message..."
            className="flex-1 p-2 rounded-md border border-gray-300 focus:outline-none focus:ring focus:border-blue-500 mb-2 sm:mb-0 sm:mr-2"
          />
          <button
            onClick={sendMessage}
            className="w-full sm:w-auto px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring focus:bg-blue-600"
          >
           <ArrowRightIcon  className="h-6 w-5"/>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
