import React, { useEffect, useState } from "react";
import { ArrowRightIcon, UserCircleIcon } from "@heroicons/react/outline";
import { MoonIcon, SunIcon } from "@heroicons/react/solid";

function Chat({ socket, username, room }) {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);
  const [istDateTime, setIstDateTime] = useState("");
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const fetchIST = () => {
      const now = new Date();
      const options = {
        hour: 'numeric',
        minute: 'numeric',
      };

      const formattedTime = now.toLocaleString('en-IN', options);
      const is12HourFormat = formattedTime.includes("AM") || formattedTime.includes("PM");

      setIstDateTime(is12HourFormat ? formattedTime : formattedTime.replace(':', '.')); // Display based on system's time format
    };

    fetchIST(); // Call fetchIST function once when the component mounts
  }, []);

  const sendMessage = async () => {
    if (currentMessage !== "") {
      const messageData = {
        room: room,
        author: username,
        message: currentMessage,
        time: istDateTime,
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
  }, [socket]);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={`min-h-screen flex flex-col relative ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-black'}`}>
      <div className={`fixed top-0 left-0 right-0 p-4 sm:p-4 ${darkMode ? 'bg-gray-800' : 'bg-gray-200'} border-b border-gray-300 z-10`}>
      <button onClick={toggleDarkMode} className={`fixed top-4 right-4 p-2 rounded-full border ${darkMode ? 'border-yellow-400 bg-gray-600' : 'border-gray-400 bg-gray-400'}`}>
  {darkMode ? (
    <SunIcon className="h-5 w-5 text-yellow-400" />
  ) : (
    <MoonIcon className="h-5 w-5 text-gray-500" />
  )}
</button>

        <h1 className={`text-xl sm:text-2xl font-semibold ${darkMode ? 'text-white' : 'text-gray-800'}`}>Strange CHAT...</h1>
      </div>

      <div className="mt-16 flex-1 overflow-y-auto p-4 sm:p-6 pb-24">
        {messageList.map((messageContent, index) => {
          const isMe = messageContent.author === username;

          return (
            <div
              key={index}
              className={`relative mb-2 ${isMe ? 'flex justify-end' : 'flex justify-start'}`}
              id={isMe ? "you" : "other"}
            >
              {!isMe && (
                <div style={{ display: 'flex' }}>
                  <UserCircleIcon className="h-6 w-6 text-gray-500" />
                </div>
              )}
              <div style={{ marginBottom: 10 }}>
                <div style={{ marginTop: 2 }} className={`bg-${isMe ? 'teal-200' : 'gray-300'} text-black py-2 px-3 rounded-lg inline-block max-w-xs break-words relative`}>
                  {!isMe && (<p style={{ textAlign: 'left', fontSize: 'small', marginTop: 1, color: 'darkgreen' }}>{messageContent.author}</p>)}
                  <div style={{ display: 'flex' }}>
                    <p style={{ textAlign: 'left', fontSize: 'medium' }}>{messageContent.message}</p>
                    <p style={{ textAlign: 'right', fontSize: 'x-small', marginTop: 'auto', paddingLeft: 10 }}>{messageContent.time}</p>
                  </div>
                  <div className={`absolute ${isMe ? 'right-0' : 'left-0'} -top-1`}>
                    <div className={`w-4 h-4 ${isMe ? 'bg-teal-200' : 'bg-gray-300'} transform rotate-180 rounded`}></div>
                  </div>

                </div>
              </div>
            </div>
          );
        })}
      </div>

      <div className={`fixed bottom-0 left-0 right-0  ${darkMode ? 'border-gray-700 bg-gray-800 text-white' : 'border-gray-300 bg-white text-black'} p-4 sm:p-4`}>
        <div className="flex flex-col sm:flex-row">
          <input
  type="text"
  value={currentMessage}
  onChange={(event) => {
    setCurrentMessage(event.target.value);
  }}
  onKeyPress={(event) => {
    event.key === "Enter" && sendMessage();
  }}
  placeholder="Type a message..."
  className={`flex-1 p-2 rounded-md border ${darkMode ? 'border-gray-700 bg-gray-800 text-white' : 'border-gray-300 bg-white text-black'} focus:outline-none focus:ring focus:border-blue-500 mb-2 sm:mb-0 sm:mr-2`}
/>
        <button
            onClick={sendMessage}
            className="w-full sm:w-auto px-3 py-1 bg-teal-700 text-white rounded-md hover:bg-teal-600 focus:outline-none focus:ring focus:bg-teal-700"
          >
            <ArrowRightIcon className="h-6 w-6" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
