// App.js

import io from "socket.io-client";
import { useState } from "react";
import Chat from "./chat";

const socket = io.connect("http://192.168.193.161:3001");

function App() {
  const [room, setRoom] = useState("");
  const [showChat, setShowChat] = useState(false);
  const username = localStorage.getItem('name');
  const photo = localStorage.getItem('photo');

  const joinRoom = () => {
    const messageData = {
      room: room,
      username: username
    };
    
    if (username !== "" && room !== "") {
      socket.emit("join_room", room);
      socket.emit("user_joined_send", messageData);
      setShowChat(true);
    }
  };

  return (
    <div className="App">
      {!showChat ? (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
          <div className="bg-white rounded shadow p-8">
            <h3 className="text-lg mb-4">Join A Chat</h3>
            <input
              type="text"
              placeholder="Room ID..."
              className="block w-full border rounded py-2 px-4 mb-4"
              onChange={(event) => {
                setRoom(event.target.value);
              }}
            />
            <button
              onClick={joinRoom}
              className="bg-teal-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Join A Room
            </button>
          </div>
        </div>
      ) : (
        <Chat socket={socket} username={username} room={room} photo={photo} />
      )}
    </div>
  );
}

export default App;
