import React from 'react';
import { GoogleLogin } from 'react-google-login';

const responseGoogle = (response) => {
  // Handle Google login response
  console.log(response);

  if (response.profileObj) {
    // Access profile details if available
    const { imageUrl, name, email } = response.profileObj;
    console.log('Profile Photo URL:', imageUrl);
    console.log('Name:', name);
    console.log('Email:', email);

    // Store user information in localStorage
    localStorage.setItem('user', JSON.stringify({ name, email, imageUrl }));
  }
};

const logout = () => {
  // Clear user data from localStorage on logout
  localStorage.removeItem('user');
};

const GoogleLoginButton = () => {
  return (
    <div>
      <GoogleLogin
        clientId="798504094336-id3r3n1ldom2rjmsa6tdp6db0oii5nct.apps.googleusercontent.com"
        buttonText="Login with Google"
        onSuccess={responseGoogle}
        onFailure={responseGoogle}
        cookiePolicy={'single_host_origin'}
      />
      <button onClick={logout}>Logout</button>
    </div>
  );
};

export default GoogleLoginButton;
